  `companyId` int NOT NULL,
  `allocatedLeaves` decimal(10,2) NOT NULL DEFAULT '0.00',
  `carryForwardFromPrevious` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT 'Carry forward balance from previous period',
  `usedLeaves` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT 'Leaves used/taken in this period',
  `maxCarryForwardLimit` decimal(10,2) DEFAULT NULL COMMENT 'Maximum days that can be carried forward (per-employee override)',
  `carryForwardExpiryDate` date DEFAULT NULL COMMENT 'Date by which carry forward must be used',
  `isProRated` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Is this allocation pro-rated for mid-year joiner',
  `proRatedDays` decimal(10,2) DEFAULT NULL COMMENT 'Pro-rated allocation for partial period',
  `enableMonthlyAccrual` tinyint(1) NOT NULL DEFAULT '0',
  `monthlyAccrualRate` decimal(10,2) DEFAULT NULL COMMENT 'Leaves to be accrued per month',
  `accrualCondition` enum('first_year','all_employees','grade_based') DEFAULT NULL COMMENT 'Condition for monthly accrual',
  `totalAccruedTillDate` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT 'Total accrued till current date',
  `allowNegativeBalance` tinyint(1) NOT NULL DEFAULT '0',
  `maxNegativeLimit` decimal(10,2) DEFAULT '0.00' COMMENT 'Maximum negative balance allowed',
  `status` enum('Active','Expired','Cancelled') NOT NULL DEFAULT 'Active',
  `allocatedDate` date NOT NULL,
  `effectiveFrom` date NOT NULL,
  `effectiveTo` date NOT NULL,
  `notifyEmployee` tinyint(1) NOT NULL DEFAULT '1',
  `notifyManager` tinyint(1) NOT NULL DEFAULT '0',
  `notes` text,
  `createdBy` int DEFAULT NULL,
  `updatedBy` int DEFAULT NULL,
  `createdAt` datetime NOT NULL,
  `updatedAt` datetime NOT NULL,
  PRIMARY KEY (`leaveAllocationId`),
  UNIQUE KEY `unique_allocation_per_employee_type_period` (`staffId`,`leaveTypeId`,`effectiveFrom`,`effectiveTo`),
  KEY `leaveTypeId` (`leaveTypeId`),
  KEY `leavePolicyId` (`leavePolicyId`),
  KEY `companyId` (`companyId`),
  KEY `createdBy` (`createdBy`),
  KEY `updatedBy` (`updatedBy`),
  CONSTRAINT `leave_allocations_ibfk_1` FOREIGN KEY (`staffId`) REFERENCES `staff_details` (`staffId`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `leave_allocations_ibfk_2` FOREIGN KEY (`leaveTypeId`) REFERENCES `leave_types` (`leaveTypeId`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `leave_allocations_ibfk_3` FOREIGN KEY (`leavePolicyId`) REFERENCES `leave_policies` (`leavePolicyId`) ON DELETE SET NULL ON UPDATE CASCADE,
  CONSTRAINT `leave_allocations_ibfk_4` FOREIGN KEY (`companyId`) REFERENCES `companies` (`companyId`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `leave_allocations_ibfk_5` FOREIGN KEY (`createdBy`) REFERENCES `users` (`userId`) ON DELETE SET NULL ON UPDATE CASCADE,
  CONSTRAINT `leave_allocations_ibfk_6` FOREIGN KEY (`updatedBy`) REFERENCES `users` (`userId`) ON DELETE SET NULL ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=62 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
